mapper.insert:
INSERT INTO
  user (
         userId
       , password
       , nickName
       , isWithDraw
       , status
       , isAdmin
       )
VALUES (
         #{userId}
       , #{password}
       , #{nickName}
       , #{isWithDraw}
       , #{status}
       , #{isAdmin}
       )

mapper.update:
UPDATE user
SET password=#{password},
nickName=#{nickName},
isWithDraw=#{isWithDraw},
status=#{status}
WHERE userId=#{userId}

mapper.select:
SELECT COUNT(id)
FROM user
WHERE userId = #{id}

mapper.update:
UPDATE user
SET password = #{password}
WHERE userId = #{userId}
